package com.juansergio.usersapijs.presentation.view.adaptersimport android.view.LayoutInflaterimport android.view.ViewGroupimport androidx.recyclerview.widget.RecyclerViewimport com.juansergio.usersapijs.Rimport com.juansergio.usersapijs.data.model.UsuariosItemimport com.juansergio.usersapijs.presentation.view.ClickListenerclass UsersAdapter(private val listener: ClickListener) : RecyclerView.Adapter<UsersViewHolder>() {    private var bearslist2: ArrayList<UsuariosItem>? = null    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): UsersViewHolder {        val layoutInflater = LayoutInflater.from(parent.context)        return UsersViewHolder(layoutInflater.inflate(R.layout.usersitem, parent, false))    }    override fun onBindViewHolder(holder: UsersViewHolder, position: Int) {        val item = bearslist2?.get(position)        holder.usersrender(item)        holder.binding.imgbear.setOnClickListener {            item?.let { it1 ->                listener.itemSelectw(it1)            }        }        holder.itemView.setOnClickListener {            // listener.itemSelectw(bearslist2!![position])        }    }    override fun getItemCount(): Int = bearslist2?.size ?: 0    fun setItems(list: MutableList<UsuariosItem>) {        bearslist2?.clear()        bearslist2?.addAll(list)        notifyDataSetChanged()    }    fun setData(list: ArrayList<UsuariosItem>) {        bearslist2 = list        notifyDataSetChanged()    }}