package com.juansergio.usersapijs.presentation.view.adaptersimport android.view.Viewimport androidx.recyclerview.widget.RecyclerViewimport com.juansergio.usersapijs.data.model.UsuariosItemimport com.juansergio.usersapijs.databinding.UsersitemBindingclass UsersViewHolder(view: View) : RecyclerView.ViewHolder(view) {    val binding = UsersitemBinding.bind(view)    fun usersrender(UsersModel: UsuariosItem?) {        binding.txttitulo.text = UsersModel?.name        binding.txtDescription.text = UsersModel?.address?.city    }}