package com.juansergio.usersapijs.presentation.view.adaptersimport android.view.Viewimport androidx.recyclerview.widget.RecyclerViewimport com.juansergio.usersapijs.data.model.UsuariosItemimport com.juansergio.usersapijs.databinding.UsersitemBindingclass UsersViewHolder(view: View): RecyclerView.ViewHolder(view){    val binding= UsersitemBinding.bind(view)    fun bearsrender(beersdModel: UsuariosItem?){       binding.txttitulo.text=beersdModel?.name        binding.txtDescription.text= beersdModel?.address?.city  //  Picasso.get().load(beersdModel?.image_url).into(binding.imgbear)    }}