package com.juansergio.usersapijs.presentation.viewmodelimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.juansergio.usersapijs.data.UsuariosResponseimport com.juansergio.usersapijs.domain.usescase.LoginUsersCaseimport kotlinx.coroutines.launchclass LoginviewModel : ViewModel() {    private val _progressState = MutableLiveData<Boolean>()    val progressState: LiveData<Boolean>get() = _progressState    private val usersCase = LoginUsersCase()    private val _stateInFO: MutableLiveData<UsuariosResponse> = MutableLiveData()    val stateINFO: LiveData<UsuariosResponse> get() = _stateInFO    fun getLoginUser(id_usuarios: String) {        _progressState.value = true        viewModelScope.launch {            var response = usersCase.getData(id_usuarios)            _stateInFO.postValue(response)        }    }}