package com.juansergio.usersapijs.presentation.viewmodelimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.juansergio.usersapijs.data.model.UsuariosItemimport com.juansergio.usersapijs.domain.usescase.GetUsersCaseimport kotlinx.coroutines.launchclass UserviewModel :ViewModel() {    private  val usersCase= GetUsersCase()    private  val _stateInFO: MutableLiveData<MutableList<UsuariosItem>> = MutableLiveData()    val stateINFO: LiveData<MutableList<UsuariosItem>> get()= _stateInFO      fun getAllUser(){        viewModelScope.launch {            val response=usersCase.getData()            if(response.isEmpty()){            }else{                _stateInFO.postValue(response)            }        }    }}